import React, { Component } from 'react'
import { NavLink } from 'react-router-dom';

export class ManageBlog extends Component {
    constructor(props) {
        super(props)
        this.state = {
            result: []
        }
    }
    componentDidMount() {
        fetch(
            "http://localhost:5000/api/blog/list")
            .then((res) => res.json())
            .then((json) => {
                this.setState({
                    result: json,
                });
                console.log("data", this.state.result);
            })
    }
    render() {
        const total = this.state.result.length;
        return (
            <React.Fragment>
                <div class="container-fluid p-0">
                    <section class="resume-section">
                        <div class="resume-section-content">
                            <h2 class="mb-5"><span className='text-primary fs-1'>M</span>anage<sup className='text-secondary'>Exist</sup>Blog</h2>
                            <div class="d-flex flex-column flex-md-row justify-content-between mb-5">
                                <div class="flex-grow-1">
                                    <h3 class="mb-0 text-dark mb-5"><u>Total:</u><span>{total}</span></h3>
                                    {
                                        this.state.result.length > 0 ? <table class="table">
                                            <thead>
                                                <tr>
                                                    <th>#</th>
                                                    <th>Title</th>
                                                    <th colSpan='3' className='text-center'>Manage</th>
                                                </tr>
                                            </thead>
                                            <tbody>
                                                {this.state.result.map((data, key = 1) => (
                                                    <tr>
                                                        <td>{key + 1}</td>
                                                        <td>{data.title}</td>
                                                        <td><button className='btn btn-outline-dark border border-0'><i class="bi bi-pencil-square h4 text-secondary"></i></button></td>
                                                        <td><button className='btn btn-outline-info border border-0'><i class="bi bi-card-list h4 text-info"></i></button></td>
                                                        <td><button className='btn btn-outline-danger border border-0'><i class="bi bi-trash h4 text-danger"></i></button></td>
                                                    </tr>
                                                ))}
                                            </tbody>
                                        </table> : <div class="">
                                            {/* <div class=""><span className='text-dark fs-1'>500<sup><i class="fa fa-cogs fs-2 text-dark"></i></sup></span></div>
                                            <span class="h2">Opps! Internal Server Error!</span><br />
                                            <span class="h5">Unfortunately we're having trouble loading the page you are looking for. Please come back in a while.</span> */}
                                            {/* <div class=""><span className='text-dark fs-1'>500<sup><i class="fa fa-cogs fs-2 text-dark"></i></sup></span></div> */}
                                            <span class="h2">Opps! No Record Found!</span><br />
                                            {/* <span class="h5">Unfortunately we're having trouble loading the page you are looking for. Please come back in a while.</span> */}
                                        </div>}
                                </div>
                                <div class="flex-shrink-0"><NavLink className='text-dark' to='/admin/createblog'><span class="fs-5">Create New Blog</span></NavLink></div>
                            </div>
                        </div>
                    </section>
                </div>
            </React.Fragment>)
    }
}

export default ManageBlog;
