import React, { Component } from 'react'
import { NavLink } from 'react-router-dom';
import axios from 'axios';
export class CreateCategory extends Component {
    constructor(props) {
        super(props)

        this.state = {
            bid: '',
            subject_name: '',
            message: ''
        }
    }
    SuccessData = () => {
        alert("success")
    }
    handleSubmit = (event) => {
        event.preventDefault();
        let data = {
            subject_name: this.state.subject_name
        }
        axios.post('http://localhost:5000/api/subject/add', { data })
            .then(res => {
                console.log(res);
                console.log(res.data);
                this.SuccessData();
            }).catch((error) => {
                console.log(error)
            })
    }
    handleChangedata = (event) => {
        const { name, value } = event.target;
        this.setState({ [name]: value }, () => { console.log(this.state) });
    }
    render() {
        return (
            <React.Fragment>
                <div class="container-fluid p-0">
                    <section class="resume-section" id="category">
                        <div class="resume-section-content">
                            <h2 class="mb-5"><span className='text-primary fs-1'>A</span>dd<sup className='text-secondary'>New</sup>Subject</h2>
                            <div class="d-flex flex-column flex-md-row justify-content-between mb-5">
                                <div class="flex-grow-1">
                                    <NavLink to='/admin/managesubject'><h3 class="mb-0 text-primary mb-5"><u>Posted Subject</u></h3></NavLink>
                                    <form className='row' onSubmit={this.handleSubmit}>
                                        <div class="mb-5 col-sm-12">
                                            <label class="form-label fs-4">Subject</label>
                                            <input type="text" class="form-control fs-4 gg" style={{ border: 'none', borderBottom: '2px solid black' }} placeholder='Enter Subject Name' name='subject_name' onChange={this.handleChangedata} />
                                        </div>
                                        <button type="submit" class="btn btn-warning text-dark fs-5 form-control p-3 ">Save</button>
                                    </form>
                                </div>
                                <div class="flex-shrink-0"><NavLink className='text-dark' to='/admin/createblog'><span class="fs-5">Create New Blog</span></NavLink></div>
                            </div>
                        </div>
                    </section>
                </div>
            </React.Fragment>
        )
    }
}

export default CreateCategory
