import React, { Component } from 'react'

export class ManageSubject extends Component {
    constructor(props) {
        super(props)

        this.state = {
            bid: '',
            subject_name: '',
            result: []
        }
    }
    componentDidMount() {
        fetch(
            "http://localhost:5000/api/subject/list")
            .then((res) => res.json())
            .then((json) => {
                this.setState({
                    result: json,
                });
                console.log("data", this.state.result);
            })
    }
    render() {

        return (
            <React.Fragment>
                <div class="container-fluid p-0">
                    <section class="resume-section">
                        <div class="resume-section-content">
                            <h2 class="mb-5"><span className='text-primary fs-1'>M</span>anage<sup className='text-secondary'>Exist</sup>Subject</h2>
                            <div class="d-flex flex-column flex-md-row justify-content-between mb-5">
                                <div class="flex-grow-1">
                                    <h3 class="mb-0 text-primary mb-5"><u>Posted Subject</u></h3>
                                    <table class="table">
                                        <thead>
                                            <tr>
                                                <th>#</th>
                                                <th>Subject</th>
                                                <th colSpan='3' className='text-center'>Manage</th>
                                            </tr>
                                        </thead>
                                        <tbody>
                                            {this.state.result.map((item, key = 1) => (
                                                <tr>
                                                    <td>{key + 1}</td>
                                                    <td>{item.subject_name}</td>
                                                    <td><button className='btn btn-outline-dark border border-0'><i class="bi bi-pencil-square h5"></i></button></td>
                                                    <td><button className='btn btn-outline-info border border-0'><i class="bi bi-card-list h4"></i></button></td>
                                                    <td><button className='btn btn-outline-danger border border-0'><i class="bi bi-trash h4"></i></button></td>
                                                </tr>
                                            ))}
                                        </tbody>
                                    </table>
                                </div>
                                <div class="flex-shrink-0"><span class="fs-5 text-muted"><u>Create New Blog</u></span></div>
                            </div>
                        </div>
                    </section>
                </div>
            </React.Fragment>
        )
    }
}

export default ManageSubject;
